SET NOCOUNT ON

SET IDENTITY_INSERT [ClientGrantType] ON

MERGE INTO [ClientGrantType] AS [Target]
USING (VALUES
  (1,N'hybrid',1)
) AS [Source] ([Id],[GrantType],[ClientId])
ON ([Target].[Id] = [Source].[Id])
WHEN MATCHED AND (
	NULLIF([Source].[GrantType], [Target].[GrantType]) IS NOT NULL OR NULLIF([Target].[GrantType], [Source].[GrantType]) IS NOT NULL OR 
	NULLIF([Source].[ClientId], [Target].[ClientId]) IS NOT NULL OR NULLIF([Target].[ClientId], [Source].[ClientId]) IS NOT NULL) THEN
 UPDATE SET
  [Target].[GrantType] = [Source].[GrantType], 
  [Target].[ClientId] = [Source].[ClientId]
WHEN NOT MATCHED BY TARGET THEN
 INSERT([Id],[GrantType],[ClientId])
 VALUES([Source].[Id],[Source].[GrantType],[Source].[ClientId])
WHEN NOT MATCHED BY SOURCE THEN 
 DELETE;


